    @Test
    public void TestMethods(){
        int[] nums = {1,2,3,3};
        boolean[] locks = new boolean[4];
        locks[2] = true;
        Map<Integer, Integer> tailCounter = new HashMap<>();
        System.out.println(createArray(nums, locks, tailCounter, 0));
        System.out.println(Arrays.toString(locks));
        System.out.println(tailCounter);
    }

    /**
     * [贪心算法1] 双HashMap, 分别记录数字个数，和每个已创建数组的尾部
     * 每次优先将当前数接至已有数组尾部，
     * 其次去检查能够创建长度为3的数组，否则，return false
     * 执行用时：34 ms, 在所有 Java 提交中击败了48.15%的用户
     * 内存消耗：40.6 MB, 在所有 Java 提交中击败了10.89%的用户
     * */
    public boolean isPossible(int[] nums) {
        // 记录每个数字出现的次数
        Map<Integer, Integer> counter = new HashMap<>();
        // 记录当前已创建数组的尾部数字，及对应数组个数
        Map<Integer, Integer> tails = new HashMap<>();
        for(int num : nums){    // 初始化计数器
            counter.put(num, counter.getOrDefault(num, 0) + 1);
        }
        System.out.println(counter);
        for (int i = 0; i < nums.length; i++) {
            // 遍历全部数组，只要当前数字未被使用完。则执行拼接数组/创建数组操作。
            if (counter.get(nums[i])!= 0){
                // 先检查是否能拼接至已有数组尾部
                if(i > 0 && counter.getOrDefault(nums[i]-1, 0) != 0){
                    counter.put(nums[i], counter.get(nums[i]) - 1);    // counter中使用nums[i]
                    // 删减拼接尾部，生成新尾部
                    tails.put(nums[i]-1,tails.get(nums[i]-1) - 1);
                    tails.put(nums[i], tails.getOrDefault(nums[i], 0) + 1);
                }else {
                    // 若不能拼接，判断能否一次性创建长度为3的数组
                    if(i + 2 < nums.length && counter.getOrDefault(nums[i] + 1, 0) > 0
                            && counter.getOrDefault(nums[i] + 2, 0) > 0){
                        counter.put(nums[i], counter.get(nums[i]) - 1);
                        counter.put(nums[i]+1, counter.get(nums[i]+1) - 1);
                        counter.put(nums[i]+2, counter.get(nums[i]+2) - 1);
                        tails.put(nums[i] + 2, tails.getOrDefault(nums[i] + 2, 0) + 1);
                    }else{
                        return false;
                    }
                }
            }
        }
        return true;
    }

    /**
     * [贪心算法]2 boolean[] + HashMap, 分别记录数字是否被使用，和每个已创建数组的尾部
     * 每次优先将当前数接至已有数组尾部，其次去检查能够创建长度为3的数组，否则，return false
     * 执行用时：7 ms, 在所有 Java 提交中击败了85.86%的用户
     * 内存消耗：39.3 MB, 在所有 Java 提交中击败了85.72%的用户
     * */
    public boolean isPossible2(int[] nums) {
        // 记录每个元素是否被使用
        boolean[] locks = new boolean[nums.length];
        // 记录当前已创建数组的尾部数字，及对应数组个数
        Map<Integer, Integer> tails = new HashMap<>();
        for (int pos = 0; pos < nums.length; pos++) {
            if(!locks[pos]){
                if(!(appendNum(nums, locks, tails, pos) || createArray(nums, locks, tails, pos))){
                    return false;
                }
            }
        }
        return true;
    }
    // 追加数字
    boolean appendNum(int[] nums, boolean[] locks, Map<Integer, Integer> tails, int pos){
        if(tails.getOrDefault(nums[pos]-1, 0) > 0){
            locks[pos] = true;
            // 删减拼接尾部，生成新尾部
            tails.put(nums[pos]-1, tails.get(nums[pos]-1) - 1);
            tails.put(nums[pos], tails.getOrDefault(nums[pos], 0) + 1);
            return true;
        }else {
            return false;
        }

    }
    // 生成数组
    boolean createArray(int[] nums, boolean[] locks, Map<Integer, Integer> tails, int pos){
        int count = 0;
        for (int i = pos; i < nums.length && count < 3; i++) {
            if(nums[pos] + count == nums[i] && !locks[i]){
                locks[i] = true;
                count++;
            }else if(nums[i] > nums[pos] + 3){
                break;
            }
        }
        if (count == 3){
            tails.put(nums[pos] + 2, tails.getOrDefault(nums[pos] + 2,0) + 1);
            return true;
        }else {
            return false;
        }
    }

    /** @充话费送qb
     * [堆的应用]HashMap + 优先队列
     * Map中, 键-队尾数字, 值-当前队尾下，队列长度。
     * 每次优先获取队列长度短的队列，进行拼接。
     * 执行用时：84 ms, 在所有 Java 提交中击败了12.12%的用户
     * 内存消耗：39.7 MB, 在所有 Java 提交中击败了61.57%的用户
     * */
    public boolean isPossible3(int[] nums) {
        HashMap<Integer, PriorityQueue<Integer>> map = new HashMap<>();
        for(int num : nums){
            if(!map.containsKey(num)){
                map.put(num, new PriorityQueue<Integer>());
            }
            // 检查是否有已构建的数组尾部可以拼接。
            if(map.containsKey(num-1)){
                int preLength = map.get(num-1).poll();
                if(map.get(num-1).isEmpty()){
                    map.remove(num-1);
                }
                map.get(num).offer(preLength+1);
            }else { // 否则创建新数组[尾部为num, 长度为1]
                map.get(num).offer(1);
            }
            System.out.println(map);
        }
        // 检查map中是否存在不合法数组
        for(PriorityQueue<Integer> queue : map.values()){
            if(queue.peek()<3){
                return false;
            }
        }
        return true;
    }

    /** @坏森生
     * 二维数组实现,arr[生成数组个数][当前数组的末位数, 当前数组长度]
     * 执行用时：29 ms, 在所有 Java 提交中击败了67.68%的用户
     * 内存消耗：39 MB, 在所有 Java 提交中击败了96.26%的用户
     * */
    public boolean isPossible4(int[] nums) {
        int len = nums.length, count = 0;
        int[][] arr = new int[len][2];
        for (int num : nums) {
            boolean flag = true;
            // 从后向前遍历已生成的数组, 寻找能追加的数组
            for (int j = count - 1; j >= 0 && flag && arr[j][0] + 1 >= num; j--) {
                if (arr[j][0] + 1 == num) {
                    arr[j][0] = num;
                    arr[j][1]++;
                    flag = false;
                }
            }
            if (flag) {     // 若不能追加，则生成新数组
                arr[count][0] = num;
                arr[count][1] = 1;
                count++;
            }
        }
        // 遍历已有数组长度
        while(count > 0){
            if(arr[--count][1] <= 2){
                return false;
            }
        }
        return true;
    }

